request = async <T>(

	ctx: BrowserContext,

	endpoint: string,

	init: Parameters<BrowserContext['request']['fetch']>[1] = {}

): Promise<T> => {
	const headers = { ...init.headers };

	const response = await ctx.request.fetch(buildUrl(endpoint), {
		...init,
		headers,
	});

	return response.json().then((data) => data.data);
}
