import { cache, createQuery } from '@farfetched/core';

import { runtypeContract } from '@farfetched/runtypes';

import { createDomain } from 'effector';

import { Array } from 'runtypes';


import { progress, Progress, progressApi } from '@/shared/api';

import { dataExtractor } from '@/shared/lib';

import {

	StandardResponse,

	getStandardResponse,

	InRoomParams,

} from '@/shared/types';


// <REVIEW>Создание домена для хранилища должна быть по пути /src/Model/{название}/state.ts </REVIEW>
export const progressDomain = createDomain();


// <REVIEW>еффект должн происходить в хранилище по пути /src/Model/{название}/effects/effect.ts</REVIEW>
const handlerFx = progressDomain.effect<

	InRoomParams,

	StandardResponse<Progress[]>,

	Error

>(progressApi.getAll);


// <REVIEW>формирование запроса должно быть в хранилище по пути /src/Model/{название}/effects/effect.ts</REVIEW>
// <REVIEW>Тип нужно выносить в файл types.ts на одном уровне с хранилищем</REVIEW>
export const query = createQuery<

	InRoomParams,

	StandardResponse<Progress[]>,

	Error,

	StandardResponse<Progress[]>,

	Progress[]

>({

	initialData: [],

	effect: handlerFx,

	contract: runtypeContract(getStandardResponse(Array(progress))),

	mapData: dataExtractor,

});


cache(query);
