import {

	Card,

	CardContent,

	CardHeader,

	Skeleton,

	Typography,

} from '@mui/material';

import * as React from 'react';


import { CommonProps } from '@/shared/types';


// <REVIEW>Описание интерфеса или типа должно быть в файле types.ts на одном уровне с компонентой</REVIEW>
export interface SkeletonTaskCardProps extends CommonProps <BODY SkeletonTaskCardProps>

export const SkeletonTaskCard: React.FC<SkeletonTaskCardProps> = React.memo(

	function SkeletonTaskCard(props) {

		const { className, contentLinesCount = 2 } = props;

		const lines = Array(contentLinesCount).fill(0);

		return (

			<Card className={className}>

				<CardHeader title={<Skeleton height='1.5em' width='50%' />} />

				<CardContent>

					{lines.map((_, i) => (

						<Typography key={i}>

							<Skeleton width='100%' />

						</Typography>

					))}

				</CardContent>

			</Card>

		);

	}

);
