import { createMutation } from '@farfetched/core';

import { runtypeContract } from '@farfetched/runtypes';

import { createEffect, sample } from 'effector';

import { Boolean } from 'runtypes';


import { invitationsApi } from '@/shared/api';

import { i18n } from '@/shared/configs';

import { notificationsModel } from '@/shared/models';

import { getStandardResponse } from '@/shared/types';


// <REVIEW>создание еффекта должно происходить в хранилище по пути /src/Model/{название}/effects/effect.ts</REVIEW>
const handlerFx = createEffect(invitationsApi.approveInvitation);


// <REVIEW>мутация должно происходить в хранилище по пути /src/Model/{название}/state.ts</REVIEW>
export const mutation = createMutation({

	effect: handlerFx,

	contract: runtypeContract(getStandardResponse(Boolean)),

});


// <REVIEW>сэмплирование должно происходить в хранилище по пути /src/Model/{название}/state.ts</REVIEW>
sample({

	clock: mutation.finished.success,

	fn: () =>

		({

			message: i18n.t('actions.approve.notifications.success', {

				ns: 'room-invitation',

			}),

			color: 'success',

		} as const),

	target: notificationsModel.create,

});


// <REVIEW>сэмплирование должно происходить в хранилище по пути /src/Model/{название}/state.ts</REVIEW>
sample({

	clock: mutation.finished.failure,

	fn: () =>

		({

			message: i18n.t('actions.approve.notifications.error', {

				ns: 'room-invitation',

			}),

			color: 'error',

		} as const),

	target: notificationsModel.create,

});
