User: React.FC = () => {
	const user = useUnit(form.fields.user);
	// <REVIEW>переменная должна иметь явный смысл в своем названии</REVIEW>
	const { t } = useTranslation('room-invitations');

	if (user.value) {
		const label = t('actions.invite_user.actions.remove-selection', {
			username: user.value.username,
		});

		return (
			<Paper>
				<TemplateUserListItem
					{...user.value}
					slots={{
						actions: (
							<IconButton onClick={user.reset as any} aria-label={label}>
								<CloseIcon />
							</IconButton>
						),
					}}
				/>
			</Paper>
		);
	}
	const label = t('actions.invite_user.fields.user');

	return (
		<UserSearch
			value={user.value}
			onChange={user.onChange}
			onBlur={user.onBlur}
			isValid={user.isValid}
			helperText={user.errorText}
			name='user'
			label={label}
			autoComplete='off'
		/>
	);
}
