OpenUpdateRoomFormMenuItem: React.FC<

	OpenUpdateRoomFormMenuItemProps

> = (props) => {
	const { className, roomId, ...rest } = props;
	const open = useUnit(openPopup);
	const { t, } = useTranslation('rooms');
	const editText = t('actions.update_room.name');

	const onClick = () => {
		open(roomId);
	};

	return (
		<MenuItem
			className={className}
			{...rest}
			onClick={onClick}
			icon={<EditIcon />}
			label={editText}
		/>
	);
}
