import {

	Record,

	Number,

	String,

	Static,

	Union,

	Literal,

	Array

} from 'runtypes';


import { DatesFiltersParams, InRoomParams } from '@/shared/types';


import { user } from '../auth';

import { tag } from '../tags';


export const taskStatus = Union(

	Literal('done'),

	Literal('in_progress'),

	Literal('review'),

	Literal('ready')

);


export type TaskStatus = Static<typeof taskStatus>;

export const statuses: TaskStatus[] = [

	'done',

	'in_progress',

	'ready',

	'review'

];


export const task = Record({

	id: Number,

	roomId: Number,

	tags: Array(tag),

	author: user,

	title: String,

	description: String.nullable(),

	status: taskStatus,

	createdAt: String,

	updatedAt: String.nullable(),

});


export interface Task extends Static<typeof task> <BODY Task>

export interface GroupedByStatusTasks <BODY GroupedByStatusTasks>

export interface GetTasksParams extends InRoomParams, DatesFiltersParams <BODY GetTasksParams>

export interface GetTaskParams extends InRoomParams <BODY GetTaskParams>

export interface CreateTaskParams

	extends Pick<Task, 'roomId' | 'title' | 'status' | 'description'> <BODY CreateTaskParams
>

export interface UpdateTaskParams

	extends Partial<Omit<CreateTaskParams, 'roomId'>>,

		InRoomParams <BODY UpdateTaskParams
>

export interface RemoveTaskParams extends InRoomParams <BODY RemoveTaskParams>
