createQueryModel = <T>(

	params: CreateQueryModelParams<T>

): QueryModel<T> => {
	const { name, defaultValue, clock, route } = params;

	const $value = createStore<T>(defaultValue);
	const $isEmpty = $value.map((value) => value === defaultValue);
	const set = createEvent<T>();
	const reset = createEvent();

	querySync({
		controls,
		clock,
		route,
		source: {
			[name]: $value,
		},
		cleanup: {
			irrelevant: false,
			empty: true,
		},
	});

	sample({
		clock: set,
		target: $value,
	});

	sample({
		clock: reset,
		target: $value.reinit!,
	});

	return {
		$value,
		$isEmpty,
		set,
		reset,

		'@@unitShape': () => {
			return {
				set,
				value: $value,
			};
		},
	};
}

