Layout: React.FC<LayoutProps> = (props) => {
	const { className, children, slots = {} } = props;

	const hasHeader = !!slots.header;
	const classes = cn(
		styles.wrapper,
		{
			[styles['wrapper--headerless']]: !hasHeader,
		},
		className
	);

	return (
		<div className={classes}>
			{slots.header}
			<main className={styles.layout}>{children}</main>
		</div>
	);
}
