getPrintingRowStatus = (

    rowIndex: number,

    currentRowIndex: number

): PrintingRowStatus => {
    //     конструкция условий должны иметь фигурреы скобки.
    //     active - должна быть константой export const ACTIVE = "active" которое должны импотнуть из пути "/src/shared/constants.ts"
    //      if (rowIndex === currentRowIndex) {
    //         return ACTIVE;
    //      }
    // </REVIEW>
    if (rowIndex === currentRowIndex) return "active"
    //     конструкция условий должны иметь фигурреы скобки.
    //     active - должна быть константой export const PRINTED = "printed" которое должны импотнуть из пути "/src/shared/constants.ts"
    //      if (rowIndex === currentRowIndex) {
    //         return PRINTED;
    //      }
    // </REVIEW>
    if (currentRowIndex > rowIndex) return "printed"

    return "default"
}
