convertMillisecondsAndDateToTime = (dateToConvert: number | Date): string => {
    const date = dateToConvert instanceof Date ? dateToConvert : new Date(dateToConvert)

    // <REVIEW>перевод к строке долджен быть String(date.getMinutes())  и получим const minutes = String(date.getMinutes()).padStart(2, "0")</REVIEW>
    const minutes = date.getMinutes().toString().padStart(2, "0")
    // <REVIEW>перевод к строке долджен быть String(date.getMinutes())  и получим const seconds = String(date.getSeconds()).padStart(2, "0")</REVIEW>
    const seconds = date.getSeconds().toString().padStart(2, "0")
    // <REVIEW>перевод к строке долджен быть String(date.getMinutes()) и получим const millisecondsEnd = String(date.getMilliseconds()).padStart(3, "0")</REVIEW>
    const millisecondsEnd = date.getMilliseconds().toString().padStart(3, "0")

    // <REVIEW>нужно использовать интерполяцию для получения string переменной из нескольких параметров, `${minutes}:${seconds}:${millisecondsEnd}`</REVIEW>
    return [minutes, seconds, millisecondsEnd].join(":")
}
