            // </REVIEW>
            if (
                keys.every(key => {
                    if (key !== "!" && key.startsWith("!"))
                        return !getIsCurrentSymbol(e, key.substring(1))
                    return getIsCurrentSymbol(e, key)
                })
            )
                handler(e)
        })
    }
