TerminalAnswer = (props: TerminalAnswerProps) => {
    const { terminalValue, answersWithComponent } = props
    const Component =
        terminalValue in answersWithComponent
            ? answersWithComponent[terminalValue as AnswersKeys]
            : null

    if (!Component && terminalValue in CommandsEnum) return null

    return (
        <Box mb={8}>
            {Component ? (
                <Component {...props} />
            ) : (
                <Text color="red.500">Command not found ("help" - list of commands)</Text>
            )}
        </Box>
    )
}
