function FilterCheckbox({ label, filter }: FilterCheckboxProps) {
  const active = useStore(filter.$active);
  const disabled = useStore(filter.$disabled);

  const handleChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    const isChecked = event.target.checked;
    {/*<REVIEW>Условный оператор должен иметь фигурные скобки</REVIEW>  */}
    if (isChecked) return filter.activate({ ctx: "result_page" });

    {/*<REVIEW>result_page нужно вынести в константу с обним уровнем с контейнером в файл types.ts</REVIEW>  */}
    filter.deactivate({ ctx: "result_page" });
  };

  return (
    <FormControlLabel
      checked={active}
      disabled={disabled}
      control={<Checkbox onChange={handleChange} />}
      label={label}
    />
  );
}
