// <REVIEW>создание еффекта должно происходить в хранилище по пути /src/Model/{название}/effects/effect.ts</REVIEW> 
const identifyUserFx = createEffect((user: User) => {

  H.identify(user.id, {

    highlightDisplayName: `${user.first_name} ${user.last_name}`,

    initialized: user.initialized,

  })

})


