AppStatusPage = () => {
  const user = useUnit($$user.query)

  if (user.pending) {
    return <Loader style={{ height: 'var(--tg-viewport-stable-height)' }} />
  }

  if (user.error) {
    return <ServiceNotWorkingPage />
  }

  if (!user.data?.initialized) {
    return <MessageRequiredPage />
  }

  return null
}
